mapper.insert:
insert into course(course_Num, course_name , teacher_Num , course_content )
values((select concat('cou', coalesce(max(substring(course_num,4)::integer),100000) + 1)
   from course),#{courseName},#{teacherNum},#{courseContent})

mapper.update:

update course
set course_Name = #{courseName},
    course_content = #{courseContent}
    where course_num = #{courseNum}
